import http from "./httpService";

const registerUser = async (user) => {
  try {
    return await http.post("/users/register", user);
  } catch (err) {
    throw err;
  }
};

const getUserByEmail = async (email) => {
  try {
    return await http.get(`/users/${email}`);
  } catch (err) {
    throw err;
  }
};

const getUserPosts = async (user_id) => {
  try {
    return await http.get("users/user/posts", { params: user_id });
  } catch (err) {
    throw err;
  }
};

const getUserProfileByUsername = async (username) => {
  try {
    return await http.get("/users/profile/username", { params: username });
  } catch (err) {
    throw err;
  }
};

const getUserPostsByUsername = async (username) => {
  try {
    return await http.get("/users/author/posts", { params: username });
  } catch (err) {
    throw err;
  }
};

export default {
  registerUser,
  getUserByEmail,
  getUserPosts,
  getUserPostsByUsername,
  getUserProfileByUsername,
};
