import React, { useState } from "react";
import { Button, Snackbar, makeStyles } from "@material-ui/core";
import MuiAlert from "@material-ui/lab/Alert";

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    "& > * + *": {
      marginTop: theme.spacing(2),
    },
  },
}));

const SnackBar = ({ err, severity, success }) => {
  const [open, setOpen] = useState(false);
  const classes = useStyles();

  console.log("snack bar", err);

  const handleClose = (event, reason) => {
    if (reason === "clickaway") return setOpen(false);
  };
  return (
    <div className={classes.root}>
      <Snackbar
        open={err ? setOpen(true) : open}
        autoHideDuration={6000}
        onClose={handleClose}
      >
        <Alert onClose={handleClose} severity={severity}>
          {err ? err : success ? "Successfuly Submited" : null}
        </Alert>
      </Snackbar>
    </div>
  );
};

export default SnackBar;
