import React, { useContext, useEffect } from "react";
import { CssBaseline, Typography } from "@material-ui/core";

import PostContext from "../context/postContext";
import Loading from "../common/Loading";
import GridComponent from "../common/Grid/Grid";
import SnackBar from "../common/SnackBar";
import useSnackState from "../hooks/useSnackState";
import Layout from "../common/Layout";

const Home = () => {
  const { posts, loading, error, success, getPosts, getSelectedPost } =
    useContext(PostContext);
  const [open, handleClose, handleOpen] = useSnackState();

  useEffect(() => {
    getPosts();
    if (error) handleOpen();
  }, []);

  if (!success && loading) return <Loading />;

  return (
    <>
      <CssBaseline />
      <Layout>
        <Typography component="div" align="center">
          <h1>Trending Posts in JavaScripit & JavaScript frameworks </h1>
        </Typography>
        <GridComponent
          items={posts}
          loading={loading}
          getSelectedPost={getSelectedPost}
        />
      </Layout>
      {(error || success) && (
        <SnackBar
          open={open}
          security={error ? "error" : "success"}
          err={error}
          success={success}
          onClose={handleClose}
        />
      )}
    </>
  );
};

export default Home;
